import React, { createContext, useState } from 'react';
export const AuthContext = createContext();
export const AuthProvider = ({children})=>{
  const [user, setUser] = useState(()=>{ const raw = localStorage.getItem('user'); return raw ? JSON.parse(raw) : null; });
  const login = (userData, token) => { localStorage.setItem('user', JSON.stringify(userData)); localStorage.setItem('token', token); setUser(userData); };
  const logout = () => { localStorage.removeItem('user'); localStorage.removeItem('token'); setUser(null); };
  return <AuthContext.Provider value={{user, login, logout}}>{children}</AuthContext.Provider>;
};
